import { useState } from "react"
import React from 'react'
import "./CSS/LoginSignup.css"

const LoginSignup = () => {
  const [state, setState] = useState("Login")

  const login = async () => {  
    console.log("Login Function Executed");  
}  

const signup = async () => {  
    console.log("Signup Function Executed");  
}
  return (
    <div className='loginsignup-container overlay'>  
      <center><h1>{state}</h1> </center> 
      <div className='loginsignup-fields'>  
        {state === "Sign Up"?<input type='text' placeholder='Your Name' />  :<></>}
        <input type='email' placeholder='Email Address' />  
        <input type='password' placeholder='Password' />  
      </div>  
      <button onClick={()=>{state=="Login"?login():Signup()}}>Continue</button> 
      {state === "Sign Up"?<h1 className='loginsignup-login'>Already have an account? <span onClick={()=>{setState("Login")}}>Login here</span></h1>  : <h1 className='loginsignup-login'>Create an account <span onClick={()=>{setState("Sign Up")}}>Click Here</span></h1> } 
      
      
      <div className='loginsignup-agree'>  
        <input type='checkbox' name='' id='' />  
      <div className='text'>By continuing, I agree to the terms of use & privacy policy.</div>  
      </div>  
    </div>  
  )
}

export default LoginSignup
